sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

    template.print('<p><em>If there are any issues about the item, kindly click this <a href="mailto:dev80684@servicenowdevelopers.com?SUBJECT=RE:[ItemTracker] Inquiry about Item with ID: '+current.getValue('number')+'">link</a> to send an email and wait for a representative to contact you.</em></p>');
	template.print('<p></p>');

})(current, template, email, email_action, event);
